<template>
	<div ref="map-container" class="location-map">
		<LMap
			v-if="targetIsVisible"
			:zoom="13"
			:center="[latitude, longitude]"
			:options="{
				zoomControl: false,
			}">
			<LTileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

			<LMarker
				:lat-lng="[latitude, longitude]"
				:options="{
					title: name,
				}">
				<LIcon
					icon-url="data:image/png;base64,"
					:icon-anchor="[12.5, 41]" />
			</LMarker>
		</LMap>
	</div>
</template>

<script lang='ts' setup>
import {
	LIcon,
	LMap,
	LMarker,
	LTileLayer,
} from '@vue-leaflet/vue-leaflet'
import { useElementVisibility } from '@vueuse/core'
import { useTemplateRef } from 'vue'

// Leaflet icon patch
import 'leaflet-defaulticon-compatibility/dist/leaflet-defaulticon-compatibility.webpack.css' // Re-uses images from ~leaflet package
import 'leaflet/dist/leaflet.css'
import 'leaflet-defaulticon-compatibility'

defineProps({
	/**
	 * The latitude of the location
	 */
	latitude: {
		type: Number,
		required: true,
	},

	/**
	 * The longitude of the location
	 */
	longitude: {
		type: Number,
		required: true,
	},

	/**
	 * The name of the location
	 */
	name: {
		type: String,
		default: '',
	},
})

// Only render the map when the component is visible.
// This is needed else Leaflet will not render the map correctly.
const target = useTemplateRef<HTMLDivElement>('map-container')
const targetIsVisible = useElementVisibility(target)
</script>

<style scoped lang="scss">
.location-map {
	position: relative;
	margin: 16px;
	border-radius: var(--border-radius-large);
	height: 250px;
	width: 400px;
}
</style>
